<node TEXT="Mastic (Pistacia lentiscus)" STYLE_REF="Species" ID="ID_1682925901" CREATED="1420659726464" MODIFIED="1429772086291">
<attribute_layout NAME_WIDTH="183" VALUE_WIDTH="159"/>
